import { LatLongCoordinates } from "./latLongCoordinates.js";
/**
 * Geocoder would be an interface that would be implemented by any class that would be used to geocode an address.
 * Namely, it would be used to convert a city name to a latitude and longitude.
 */
export interface Geocoder {
    /**
     * Given a city name, return the latitude and longitude of the city.
     * @param query
     */
    searchCoords(query: string): Promise<LatLongCoordinates | null>;
}
