import { IpWithCoordinates, NewProxy } from "./proxy.js";
export abstract class ProxyTester {
  abstract getCurrentIpAddress(proxy: NewProxy): Promise<IpWithCoordinates>;
  async isProxyAvailable(proxy: NewProxy, cityName: string) {
    try {
      const ipAddressWithCoords = await this.getCurrentIpAddress(proxy);
      return ipAddressWithCoords.cityName === cityName;
    } catch (e) {
      return false;
    }
  }
}
